#include <stdio.h>

int main() {
    // Simple array demonstration
    int arr[5] = {1, 2, 3, 4, 5};

    printf("Array elements:\n");
    for (int i = 0; i < 5; i++) {
        printf("arr[%d] = %d\n", i, arr[i]);
    }

    return 0;
}

#include <stdio.h>

int main() {
    int arr[5] = {10, 20, 30, 40, 50};
    int n = 5;

    // Linear search in array
    int target = 30;
    for (int i = 0; i < n; i++) {
        if (arr[i] == target) {
            printf("Found %d at index %d\n", target, i);
            break;
        }
    }

    return 0;
}

#include <stdio.h>
#include <stdlib.h>

struct Node {
    int data;
    struct Node* next;
};

void printList(struct Node* head) {
    struct Node* temp = head;
    while (temp != NULL) {
        printf("%d -> ", temp->data);
        temp = temp->next;
    }
    printf("NULL\n");
}

int main() {
    struct Node* head = malloc(sizeof(struct Node));
    head->data = 1;
    head->next = malloc(sizeof(struct Node));
    head->next->data = 2;
    head->next->next = NULL;

    printList(head);
    return 0;
}

#include <stdio.h>

int main() {
    // Variable declarations
    int age = 21;
    float gpa = 8.5;
    char grade = 'A';
    double pi = 3.14159265;

    printf("Age: %d\n", age);
    printf("GPA: %.1f\n", gpa);
    printf("Grade: %c\n", grade);
    printf("Pi: %.8f\n", pi);

    return 0;
}

#include <stdio.h>

int main() {
    // For loop example
    printf("Counting 1 to 5:\n");
    for (int i = 1; i <= 5; i++) {
        printf("%d ", i);
    }
    printf("\n");

    // If-else example
    int marks = 85;
    if (marks >= 90) {
        printf("Grade: A+\n");
    } else if (marks >= 80) {
        printf("Grade: A\n");
    } else {
        printf("Grade: B\n");
    }

    return 0;
}
